css:
.card {
 font-family: arial;
 font-size: 20px;
 text-align: center;
 color: black;
 background-color: white;
}

.card1,.card3, .card5 { background-color: #B60F2D; }
.card2,.card4, .card6 { background-color: #2E9017; }
.card7 { background: linear-gradient(90deg, #B60F2D 49.9%, #2E9017 50.1%); }

.word {
 font-size:1.5em;
}

.pronunciation{
 color:blue;
}

.extrainfo{
 color:lightgrey;
}

tmpls.afmt:
{{#X Word}}
	<span class="word">{{X Word}}</span>
{{/X Word}}

<hr id=answer>

{{Picture}}

{{#X Pronunciation (Recording and/or IPA)}}
	<br><span class="pronunciation">{{X Pronunciation (Recording and/or IPA)}}</span>
{{/X Pronunciation (Recording and/or IPA)}}

<br>
{{#Extra}}
	<br><span class="extrainfo">{{Extra}}</span>
{{/Extra}}

{{#Y Word}}
	<span class="word">{{Y Word}}</span>
{{/Y Word}}

<hr id=answer>

{{Picture}}

